// ==UserScript==
// @author          Oleg Valter
// @description     Fix chat Stack Exchange community search without having to wait 6 to 8 weeks
// @grant           none
// @homepage        https://github.com/userscripters/chat-search-fixup#readme
// @match           https://chat.stackexchange.com
// @match           https://chat.stackoverflow.com
// @name            chat--search-fixup
// @namespace       userscripters
// @source          git+https://github.com/userscripters/chat-search-fixup.git
// @supportURL      https://github.com/userscripters/chat-search-fixup/issues
// @version         1.0.0
// ==/UserScript==

"use strict";!function(t,n){const s="js-site-filter-txt";t.addEventListener("load",()=>{$.ajaxSetup({converters:{"text html":t=>t.replace(new RegExp(`(<input)(.+class=".*?)\s?${s}`,"g"),`$1 id="${s}"$2`)}});var t=n.createElement("style");n.head.append(t);const{sheet:e}=t;e&&[`.s-input.s-input__search,
             .s-input.s-input__creditcard {
               padding-left: 32px !important;
               box-sizing: border-box;
            }`].forEach(t=>e.insertRule(t))})}(window,document);